import React from 'react';
import logo from './logo.svg';
import './App.css';
import { BrowserRouter, Switch, Route } from 'react-router-dom'
import {Component} from 'react';

/* Importing bootstrap */
import 'bootstrap/dist/css/bootstrap.min.css';

/* Importing my components from /src/components */
import Welcome from './components/welcome';
import About from './components/about';
import Contact from './components/contact';
import Projects from './components/projects';

import ScrollableAnchor, { goToTop, goToAnchor, configureAnchors, removeHash } from 'react-scrollable-anchor'


class App extends Component {

    constructor(props) {
      super(props);
      this.state = {
        text_shown: false,
        fetures_header_shown: false,
        arrow_shown: false
      };

      removeHash()
      configureAnchors({offset: 0, scrollDuration: 20})

      window.scrollTo(0, 0);


      /* Scorlling to top on page refresh */
      goToAnchor('welcome')
      window.scrollTo(0, 0);

      setTimeout(
        function() {
          configureAnchors({offset: -60, scrollDuration: 250})
          goToAnchor('about')
        }
        .bind(this),
        2100
      );

    }

    componentWillReceiveProps () {
  const element = ReactDOM.findDOMNode(this);
  if (element != null) {
    this.scrollPosition = window.scrollY
  }
}

    render() {
      return (
        <div className="App">

          <div className="scroll-to-top col-md-12" ref="topka"></div>

          <ScrollableAnchor id={'welcome'}>
            <div className="top-nav">
              <div className="top-nav-wrap">

                <a href="/" className="first-option option">
                  home
                </a>

                <a href="/about" className="option">
                  about my work
                </a>

                <a href="/projects" className="option">
                  my projects
                </a>

                <a href="/contact" className="option">
                  contact me
                </a>

              </div>
            </div>
          </ScrollableAnchor>


          <Welcome/>


          <ScrollableAnchor id={'about'}>
            <div className="components-margin scroll_to_welcome"></div>
          </ScrollableAnchor>

          <About/>

          <div className="components-margin"></div>

          <ScrollableAnchor id={'projects'}>
            <Projects/>
          </ScrollableAnchor>

          <div className="components-margin-sm"></div>

          <ScrollableAnchor id={'contact'}>
            <Contact/>
          </ScrollableAnchor>



          {/*<BrowserRouter>
            <Switch>
              <Route exact path="/projects" component={projects}/>
              <Route exact path="/" component={welcome}/>
              <Route exact path="/about" component={about}/>
              <Route exact path="/contact" component={contact}/>
            </Switch>
          </BrowserRouter>*/}

        </div>
      );
    }
}

export default App;
