import React, {Component} from 'react';
import './card.css'


import Flipcard from '@kennethormandy/react-flipcard'
import '@kennethormandy/react-flipcard/dist/Flipcard.css'


export default class Card extends Component {
    constructor(props) {
     super(props);
     console.log(props.FrontImageSource)
     this.state = {
        flipped: false,
      }
    }
    render() {
      return (
        <div className="card-cont">


          <Flipcard flipped={this.state.flipped}
            className="square"
            onMouseEnter={e => this.setState({ flipped: true })}
            onMouseLeave={e => this.setState({ flipped: false })}
          >

          <div className="content front" key="front">
            <div className="table">
              <div className="table-cell">
                <img src={process.env.PUBLIC_URL + props.img_src} className="img-fluid front-image"/>
              </div>
            </div>
          </div>

              <div>
                <h2>Column 2</h2>
                <p>Flip to see me on smaller viewports.</p>
              </div>

          </Flipcard>

        </div>
      );
    }
  }
