import React, {Component} from 'react';
import './card.css'


import Flipcard from '@kennethormandy/react-flipcard'
import '@kennethormandy/react-flipcard/dist/Flipcard.css'


export default class Card extends Component {
    constructor(props) {
     super(props);

     this.state = {
        flipped: false,
        FrontImageSource: props.FrontImageSource,
        BackText: props.BackText,
      }
    }
    render() {
      return (
        <div className="card-wrap col-md-3"
          onMouseEnter={e => this.setState({ flipped: true })}
          onMouseLeave={e => this.setState({ flipped: false })}
        >


          <Flipcard flipped={this.state.flipped}>

              <div className="content front" >
                <img src={this.state.FrontImageSource} />
              </div>

              <div className="content back">
                <h2>Column 2</h2>
                <p>Flip to see me on smaller viewports.</p>
              </div>

          </Flipcard>

        </div>
      );
    }
  }
