import React, {Component} from 'react';
import './projects.css'

/* Importing library for DOM elements animations */
import Fade from 'react-reveal/Fade';
import Flip from 'react-reveal/Flip';
import Rotate from 'react-reveal/Rotate';
import Bounce from 'react-reveal/Bounce';
import Roll from 'react-reveal/Roll';
import Zoom from 'react-reveal/Zoom';
import Slide from 'react-reveal/Slide';
import RubberBand from 'react-reveal/RubberBand';
import Spin from 'react-reveal/Spin';

/* Imaporting card flip component */
import Card from '../card';


export default class projects extends Component {

    constructor() {
      super();
        this.state = {
        ShowProjects: false
      };

      this.handleClick = this.handleClick.bind(this);

      setTimeout(
        function() {
          this.setState({ShowProjects: true});
        }
        .bind(this),
        300
      );

      setTimeout(
        function() {
          this.setState({isFlipped: true});
        }
        .bind(this),
        1625
      );

    }

    handleClick(e) {
      e.preventDefault();
      this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
    }

    render() {

      return (
        <div className="component-projects">
          <div className="container">

            <div className="row">

              <header className="col-12 projects-header">
                <h1 className="font-weight-600"> Here you can view my projects! </h1>
                <h4> Hover squares to see addional information, click them to get redirected to full description! </h4>
              </header>

            </div>

            <main>

            {this.state.ShowProjects &&
              <div className="row projects-icons-wrapper">
