import React from 'react';
import './App.css';
import {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import Welcome from './components/welcome';
import About from './components/about';
import Contact from './components/contact';
import Projects from './components/projects';

import ScrollableAnchor, { goToAnchor } from 'react-scrollable-anchor'
import NavBar from 'react-responsive-menubar/lib/NavBar';


class App extends Component {

    constructor(props) {
      super(props);
      this.state = {
        text_shown: false,
        fetures_header_shown: false,
        arrow_shown: false
      };
    }

    render() {
      return (
        <div className="App">

          <div className="scroll-to-top col-md-12"></div>

          {/* Welcome */}
          <ScrollableAnchor id={'welcome'}>
            <div className="nothing"></div>
          </ScrollableAnchor>

          <div className="top-nav">
              <div className="top-nav-wrap">



              </div>
          </div>

          <header className="header" style={{position:"relative"}}>
            <NavBar
              handleSideNavToggle={this.handleSideNavToggle}
              logo={'https://picsum.photos/200/300?image=10'}
              showSideNav={this.state.showSideNav}
              logoStyles={{height:"65px", width:"85px"}}
              navBarStyles={{boxShadow:"none"}}
              linkStyles={{color:"green", fontWeight:"bold"}}>

                <span className="first-option option"
                  onClick={() => goToAnchor('welcome') }>
                  home
                </span>

                <span href="/about" className="option"
                  onClick={() => goToAnchor('about') }>
                  about my work
                </span>

                <span href="/projects" className="option"
                  onClick={() => goToAnchor('projects') }>
                  my projects
                </span>

                <span href="/contact" className="option"
                  onClick={() => goToAnchor('contact') }>
                  contact me
                </span>

              </NavBar>
          </header>


          <Welcome/>
          <div className="scroll_to_welcome"></div>
          <ScrollableAnchor id={'about'}>
            <div className="spacer-about"></div>
          </ScrollableAnchor>
          <About/>

          <div className="projects-about-spacer"></div>

          <ScrollableAnchor id={'projects'}>
            <div className="spacer-projects"></div>
          </ScrollableAnchor>
          <Projects/>

          <ScrollableAnchor id={'contact'}>
            <div className="spacer-contact"></div>
          </ScrollableAnchor>
          <Contact/>

        </div>
      );
    }
}

export default App;
