import React, {Component} from 'react';
import './about.css'

export default class about extends Component {

    constructor(props) {
      super(props);
      this.state = {
        text_shown: false,
        fetures_header_shown: false,
        arrow_shown: false
      };
      this.scroll_to_first_feature = React.createRef();
      this.scroll_to_top = React.createRef();
    }

    /* Handling click, to scroll to scroll_to_first_feature element */
    HeaderClickEvent() {
      this.scroll_to_first_feature.current.scrollIntoView({behavior: 'smooth'});
    }

    render() {

      function ReadMore(props){
        return(
          <div className="feature-read-more">
            <a href={props.url}> Read more  </a>
          </div>
        );
      }

      function Feature(props){

        return(

            <div className="Feature">

              <div className="small">

                <div className="col-12 feature-container">


                  <section className="col-12 feature-text">

                    <header className="feature-header">
                      {props.header_text}
                    </header>

                    <img src={props.img_src} className="img-fluid feature-image col-6 col-md-3 " alt="" />

                    <div className="col-md-1 spacer"></div>
                    <div className="feature-text col-md-12">
                      {props.text}
                    </div>
                    <div className="col-md-1 spacer"></div>
                  </section>

                  <div className="clear-both"></div>

                </div>
              </div>
              <div className="large">
              <div className="col-12 feature-container">

                <img src={props.img_src} className="feature-image col-md-4 col-lg-3 col-xl-2" alt="" />

                <section className="col-md-7 col-lg-9 col-xl-10 feature-text">
                  <header className="feature-header">
                    {props.header_text}
                  </header>

                  <div className="feature-text ">
                    {props.text}
                  </div>
                </section>
                <div className="clear-both"></div>

              </div>
              </div>


            </div>






        );
      }

      return (

        <div className="component-about">
          <div className="about-content-wrapper">
            <div className="container">
              <div className="row">

                <header className="more-header-container col-12">
                  <h1 className="more-header">
                    <Roll right>
                      Here you can learn more about my work:
                    </Roll>
                  </h1>
                </header>


                <section className="text-about col-12">
                  <Bounce left>
                      I use stack of open-source technologies, that you probably faced in Internet without knowing it. Every technology I use is open-source, it means me and any other programer on the earth can improve it -  that's why they look like this today.
                      Below you can read about my technical skills (my favourite web development technologies).
                  </Bounce>
                </section>

                <div className="spacer col-2" > </div>
                <header className="show_features col-8" onClick={this.HeaderClickEvent.bind(this)}>
                  <h1 className="more-header">
                    <SplitText initialPose="exit" pose="enter" charPoses={charPosesHeader} pose={this.state.fetures_header_shown ? 'enter' : 'exit'}>
                      Technologies that I use:
                    </SplitText>

                    { this.state.arrow_shown ?
                      <div className="bounce" >
                        <img alt="" src="arrow.png" className="img-fluid bounce-arrow" />
                      </div>
                    : null }

                  </h1>
                </header>
                <div className="col-2"> </div>


                <div className="features-spacer" ref={this.scroll_to_first_feature}></div>

                  {/* design things */}
                  <Feature
                    header_text={"HTML5, CSS3, ES6:"}
                    text={"HTML5, CSS3, and ES6 are newest versions of standard, core webdevelopment technologies. HTML5, CSS3 and ES6 knowledge are core skill for every webdeveloper that allows to make website work on every browser, on every device."}
                    img_src={"h5c3js.png"}
                  />
                  <Feature
                    header_text={"Bootstrap:"}
                    text={"Bootstrap is most popular web-design tool - it allows me to organize my designing work pretty well. Using this framework also makes final website more scalable - its easier to expand it in the future. Additionally it allows me to make page responsive - it will look well on every device and resolution"}
                    img_src={"bs.png"}
                  />

                  {/* frontend things */}
                  <Feature
                    header_text={"React:"}
                    text={"React is javascript library used to create user interfaces - with react I can create flexible, responsive and scalable design. React contains a lot of tools that allows me to develop website frontend logic, and npm packages that helps me to develop frontend overall"}
                    img_src={"react.png"}
                  />

                  <Feature
                    header_text={"Vue:"}
                    text={"Vue is javascript library - it's pretty similiar to React, however there are some technical differences. I am able to create the same website with Vue and React, when I know what I have to do I will choose the best of the two ones."}
                    img_src={"vue.png"}
                  />

                  {/* backend things */}
                  <Feature
                    header_text={"Django:"}
                    text={"Django is python based backend framework. I use it to manage things like database, user authentication, computing and caching data, reading external APIs (like google places api) and sending final data to frontend to display it to user."}
                    img_src={"django_icon.jpg"}
                  />

                  <Feature
                    header_text={"Django Rest Framework:"}
                    text={"Django Rest Framework (DRF) is Django module that allows me to connect django and frontend library together - React or Vue can create, read, update or delete database data after authentication and verification. The same DRF project can be used with any frontend library in exactly the same way."}
                    url={"/ReadMore/responsive_design"}
                    img_src={"drf.png"}
                  />

                  <Feature
                    header_text={"Wagtail:"}
                    text={"Wagtail is Django module that I use when I don't need to use frontend library - Django + Wagtail app can work pretty well, with very fast load times without React or Vue. Likewise Wagtail provides beautiful editor panel."}
                    img_src={"wagtail.png"}
                  />

                  {/* Organization things */}
                  <Feature
                    header_text={"Git:"}
                    text={"Git is version control system for tracking changes in source code during software development - Wikipedia says. Software development is much more safe, and much more organized with git. Every changes in code can be easily reversed."}
                    img_src={"giticon.png"}
                  />



                <div className="features-spacer-down"></div>

              </div>
            </div>
          </div>
        </div>
      );
    }

  }
