import React, {Component} from 'react';
import './card.css'


import Flipcard from '@kennethormandy/react-flipcard'
import '@kennethormandy/react-flipcard/dist/Flipcard.css'


/* Importing library for DOM elements animations */
import Fade from 'react-reveal/Fade';
import Flip from 'react-reveal/Flip';
import Rotate from 'react-reveal/Rotate';
import Bounce from 'react-reveal/Bounce';
import Roll from 'react-reveal/Roll';
import Zoom from 'react-reveal/Zoom';
import Slide from 'react-reveal/Slide';
import RubberBand from 'react-reveal/RubberBand';
import Spin from 'react-reveal/Spin';


export default class Card extends Component {
    constructor(props) {
     super(props);

     this.state = {
        flipped: false,
        FrontImageSource: props.FrontImageSource,
        BackText: props.BackText,
        Title: props.Title,
      }

      setTimeout(
        function() {
          console.log("JD")
          this.setState({flipped: true});
        }
        .bind(this),
        0
      );
      setTimeout(
        function() {
          console.log("JD")
          this.setState({flipped: false});
        }
        .bind(this),
        270
      );



    }
    render() {
      return (
        <div className="card-wrap col-md-3"
          onMouseEnter={e => this.setState({ flipped: !this.state.flipped })}
          onMouseLeave={e => this.setState({ flipped: !this.state.flipped })}
        >


          <Flipcard flipped={this.state.flipped}>

              <div className="content front square-box" >

                  <img src={this.state.FrontImageSource} />

              </div>

              <div className="content back square-box">
                <div className='square-content'>

                  <div className="back-wrapper">
                    <h2 className="BackHeader"> {this.state.Title} </h2>
                    <div className="BackText"> {this.state.BackText} </div>

                    <h5 className="BackSpacer"> Click to learn more! </h5>
                  </div>

                </div>
              </div>

          </Flipcard>

        </div>
      );
    }
  }
