import React, {Component} from 'react';
import './welcome.css'

/* Importing Paticles.js to make welcome background moge interesing */
import Particles from "react-particles-js";
import particlesOptions from './particles_settings.json'

/* Importing library for text animations */
import SplitText from 'react-pose-text';


import { goToAnchor } from 'react-scrollable-anchor'


/* Settings for text animations */
const charPoses = {
  exit: { opacity: 0, y: 20 },
  enter: {
    opacity: 1,
    y: 0,
    delay: ({ charIndex }) => charIndex * 30
  }
};


export default class welcome extends Component {

    constructor(props) {
      super(props);
      this.state = {
        learn_more_shown: false,
      };

      document.body.style.overflow = "hidden";

      setTimeout(
        function() {
          this.setState({learn_more_shown: true});
        }
        .bind(this),
        1700
      );

      setTimeout(
        function() {
          this.setState({arrow_shown: true});
        }
        .bind(this),
        2550
      );

    }

    render() {
      return (
        <div className="component-welcome">
          <div className="container">
            <div className="row">

              <Particles className="particles" params={particlesOptions} />

              <div className="col-md-1"></div>
              <header className="welcome-header col-md-10">
                <h1 className="welcome-h1">
                  <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
                    Hello, my name is [[kox koder]] and I am full stack developer
                  </SplitText>
                </h1>
              </header>
              <div className="col-md-1"></div>


              <div className="col-md-3"></div>
              <nav className="more-about col-md-6">
                <span onClick={() => goToAnchor('about') }>
                  <header className="more-about-header">
                    <h3>
                      <SplitText initialPose="exit" pose="enter" charPoses={charPoses} pose={this.state.learn_more_shown ? 'enter' : 'exit'}>
                        Learn more about my work!
                      </SplitText>
                    </h3>
                  </header>

                  { this.state.arrow_shown ?
                    <div className="bounce" >
                      <img alt="" src="arrow.png" className="img-fluid bounce-arrow" />
                    </div>
                  : null }

                </span>
              </nav>
              <div className="col-md-3"></div>

            </div>
          </div>
        </div>
      );
    }
  }
