import React, {Component} from 'react';
import './about.css'

/* Importing library for text animations */
import SplitText from 'react-pose-text';

/* Importing library for DOM elements animations */
import Fade from 'react-reveal/Fade';
import Bounce from 'react-reveal/Bounce';
import Roll from 'react-reveal/Roll';


/* Settings for text animations */
const charPosesHeader = {
  exit: { opacity: 0, y: 20 },
  enter: {
    opacity: 1,
    y: 0,
    delay: ({ charIndex }) => charIndex * 45
  }
};

export default class about extends Component {

    constructor(props) {
      super(props);
      this.state = {
        text_shown: false,
        fetures_header_shown: false,
        arrow_shown: false
      };

      this.handleScroll = this.handleScroll.bind(this);

      /* Setting ref for scroll_to_first_feature  */
      this.scroll_to_first_feature = React.createRef();

      /* Setting ref for scroll_to_top */
      this.scroll_to_top = React.createRef();

      /* Calling animations after some time */

      setTimeout(
        function() {
          this.setState({text_shown: true});
        }
        .bind(this),
        1625
      );



    }

    /* Setting up onscroll event handling */
    componentWillMount(){
      window.removeEventListener('scroll', this.handleScroll);
    }
    componentDidMount(){
      window.addEventListener('scroll', this.handleScroll);
    }

    handleScroll(event){
      /* Showing features header, and arrow on scroll after short dealay */
      setTimeout(
        function() {
          this.setState({fetures_header_shown: true});
        }
        .bind(this),
        550
      );
      setTimeout(
        function() {
          this.setState({arrow_shown: true});
        }
        .bind(this),
        1775
      );
    }

    /* Handling click, to scroll to scroll_to_first_feature element */
    HeaderClickEvent() {
      this.scroll_to_first_feature.current.scrollIntoView({behavior: 'smooth'});
    }

    render() {

      function ReadMore(props){
        return(
          <div className="feature-read-more">
            <a href={props.url}> Read more  </a>
          </div>
        );
      }

      function Feature(props){

        return(

            <div className="Feature">

              <div className="small">

                <div className="col-12 feature-container">


                  <section className="col-12 feature-text">

                    <header className="feature-header">
                      {props.header_text}
                    </header>

                    <img src={props.img_src} className="img-fluid feature-image col-6 col-md-3 " alt="" />

                    <div class="col-md-1 spacer"></div>
                    <div className="feature-text col-md-12">
                      {props.text}
                    </div>
                    <div class="col-md-1 spacer"></div>
                  </section>

                  <div className="clear-both"></div>

                </div>
              </div>
              <div className="large">
              <div className="col-12 feature-container">

                <img src={props.img_src} className="feature-image col-md-4 col-lg-3 col-xl-2" alt="" />

                <section className="col-md-7 col-lg-9 col-xl-10 feature-text">
                  <header className="feature-header">
                    {props.header_text}
                  </header>

                  <div className="feature-text ">
                    {props.text}
                  </div>
                </section>
                <div className="clear-both"></div>

              </div>
              </div>


            </div>






        );
      }

      return (

        <div className="component-about">
          <div className="about-content-wrapper">
            <div className="container">
              <div className="row">

                <header className="more-header-container col-12">
                  <h1 className="more-header">
                    <Roll right>
                      Here you can learn more about my work:
                    </Roll>
                  </h1>
                </header>


                <section className="text-about col-12">
                  <Bounce left>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis.
                    In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero,
                    ut molestie neque laoreet quis. In sollicitudin placerat lobortis.
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis.
                    In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero,
                    ut molestie neque laoreet quis. In sollicitudin placerat lobortis.
                  </Bounce>
                </section>

                <div className="spacer col-2" > </div>
                <header className="show_features col-8" onClick={this.HeaderClickEvent.bind(this)}>
                  <h1 className="more-header">
                    <SplitText initialPose="exit" pose="enter" charPoses={charPosesHeader} pose={this.state.fetures_header_shown ? 'enter' : 'exit'}>
                      Technologies that I use:
                    </SplitText>

                    { this.state.arrow_shown ?
                      <div className="bounce" >
                        <img alt="" src="arrow.png" className="img-fluid bounce-arrow" />
                      </div>
                    : null }

                  </h1>
                </header>
                <div className="col-2"> </div>


                <div className="features-spacer" ref={this.scroll_to_first_feature}></div>

                  {/* design things */}
                  <Feature
                    header_text={"HTML5, CSS3, ES6:"}
                    text={"HTML5, CSS3, and ES6 are newest versions of standard, core webdevelopment technologies. Those 3 languages are used to create any website - every website you have ever seen"}
                    img_src={"h5c3js.png"}
                  />
                  <Feature
                    header_text={"Bootstrap:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    img_src={"bs.png"}
                  />

                  {/* frontend things */}
                  <Feature
                    header_text={"React:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    img_src={"react.png"}
                  />

                  <Feature
                    header_text={"Vue:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    img_src={"vue.png"}
                  />

                  {/* backend things */}
                  <Feature
                    header_text={"Django:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    img_src={"django_icon.jpg"}
                  />

                  <Feature
                    header_text={"Django Rest Framework:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    url={"/ReadMore/responsive_design"}
                    img_src={"drf.png"}
                  />

                  <Feature
                    header_text={"Wagtail:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    img_src={"wagtail.png"}
                  />

                  {/* Organization things */}
                  <Feature
                    header_text={"Git:"}
                    text={"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent pretium lacinia libero, ut molestie neque laoreet quis. In sollicitudin placerat lobortis."}
                    img_src={"giticon.png"}
                  />



                <div className="features-spacer-down"></div>

              </div>
            </div>
          </div>
        </div>
      );
    }

  }
